import React, { useState } from "react";
import pic from "./images/img.png";
import Add from "../src/images/Add.png";
import "./App.css";
import Timer from "./Timer";
import Footer from "./Footer";
const App = () => {
    const [inputList, setinputList] = useState("Hello Buddy");
    const [items, setItems] = useState([]);
    const inputEvent = (event) => {
        setinputList(event.target.value);
    };
//   const inputList = () => {};
const listOfItems=()=>{
    setItems((oldItems)=>{
        return [...oldItems, inputList]
    })
    setinputList("")

}
    return (
        <>
            <div className="main_div">
                <div className="part1">
                    <div className="img">
                        <img className="imgstyle" src={pic} alt="pic" />
                    </div>
                    <div className="logo">
                        <h1>Task-List</h1>
                    </div>
                    <div className="timer">
                        <Timer />
                    </div>
                    <div className="Mid_section"></div>
                </div>
                <div className="mid_content">
                    <div className="todolist">
                            <div className="top_head">
                                <div className="header">
                                    <h1>Today's Routine!</h1>
                                </div>
                                <div className="dayAndDate">
                                    <span className="day">Day of the week</span>
                                    <span className="date">date of the Month</span>

                                </div>
                            </div>

                            <div className="input_feild">
                                <input className="input_section" type="text" placeholder="Add a Task"
                                onChange = {inputEvent}/>

                              <button className="btn" onClick={listOfItems}><img className="img_style" src={Add} alt="pic2" /></button> 
                            </div>   
                    </div>
                    <div className="result">
                        <ul>
                            {/* <li>
                                {inputList}
                            </li> */}
                           { items.map((itemsvalue) =>{
                                return <li>{itemsvalue}</li>}
                           )}
                        </ul>
                        
                    </div>
                </div>
                <div className="footer">
                    <Footer />
                </div>
            </div>
        </>
    );
};
export default App;
